mapper.select:
select 

from invoice
where AuditStateId = #{auditstateid,jdbcType=INTEGER}
  and Auditor1Id = #{auditor1id,jdbcType=INTEGER}
  and Auditor2Id = #{auditor2id,jdbcType=INTEGER}
  and DearerId = #{dearerid,jdbcType=INTEGER}
  and Id = #{id,jdbcType=INTEGER}
  and InvoiceDetailsId = #{invoicedetailsid,jdbcType=INTEGER}
  and InvoiceTypeId = #{invoicetypeid,jdbcType=INTEGER}
  and OperatorId = #{operatorid,jdbcType=INTEGER}
  and ProverId = #{proverid,jdbcType=INTEGER}

mapper.delete:
delete from invoice
where AuditStateId = #{auditstateid,jdbcType=INTEGER}
  and Auditor1Id = #{auditor1id,jdbcType=INTEGER}
  and Auditor2Id = #{auditor2id,jdbcType=INTEGER}
  and DearerId = #{dearerid,jdbcType=INTEGER}
  and Id = #{id,jdbcType=INTEGER}
  and InvoiceDetailsId = #{invoicedetailsid,jdbcType=INTEGER}
  and InvoiceTypeId = #{invoicetypeid,jdbcType=INTEGER}
  and OperatorId = #{operatorid,jdbcType=INTEGER}
  and ProverId = #{proverid,jdbcType=INTEGER}

mapper.update:
update invoice


    CreateDate = #{createdate,jdbcType=TIMESTAMP},


    OccurDate = #{occurdate,jdbcType=TIMESTAMP},


    Content = #{content,jdbcType=VARCHAR},


    Money = #{money,jdbcType=DECIMAL},


    Penalty = #{penalty,jdbcType=DECIMAL},


    Description = #{description,jdbcType=VARCHAR},


    ProjectName = #{projectname,jdbcType=VARCHAR},


    PhotoUrl = #{photourl,jdbcType=VARCHAR},


    Remark = #{remark,jdbcType=VARCHAR},


where AuditStateId = #{auditstateid,jdbcType=INTEGER}
  and Auditor1Id = #{auditor1id,jdbcType=INTEGER}
  and Auditor2Id = #{auditor2id,jdbcType=INTEGER}
  and DearerId = #{dearerid,jdbcType=INTEGER}
  and Id = #{id,jdbcType=INTEGER}
  and InvoiceDetailsId = #{invoicedetailsid,jdbcType=INTEGER}
  and InvoiceTypeId = #{invoicetypeid,jdbcType=INTEGER}
  and OperatorId = #{operatorid,jdbcType=INTEGER}
  and ProverId = #{proverid,jdbcType=INTEGER}

update invoice
set CreateDate = #{createdate,jdbcType=TIMESTAMP},
  OccurDate = #{occurdate,jdbcType=TIMESTAMP},
  Content = #{content,jdbcType=VARCHAR},
  Money = #{money,jdbcType=DECIMAL},
  Penalty = #{penalty,jdbcType=DECIMAL},
  Description = #{description,jdbcType=VARCHAR},
  ProjectName = #{projectname,jdbcType=VARCHAR},
  PhotoUrl = #{photourl,jdbcType=VARCHAR},
  Remark = #{remark,jdbcType=VARCHAR}
where AuditStateId = #{auditstateid,jdbcType=INTEGER}
  and Auditor1Id = #{auditor1id,jdbcType=INTEGER}
  and Auditor2Id = #{auditor2id,jdbcType=INTEGER}
  and DearerId = #{dearerid,jdbcType=INTEGER}
  and Id = #{id,jdbcType=INTEGER}
  and InvoiceDetailsId = #{invoicedetailsid,jdbcType=INTEGER}
  and InvoiceTypeId = #{invoicetypeid,jdbcType=INTEGER}
